mapper.select:
select * from(
	select t.*, ROWNUM row_num from(
		select 
			count(*)over () DATA_TOTAL,

			from SYS_FUNC


				and ID = #{id,jdbcType=DECIMAL}


				and LEVEL_1_POS =#{lev1,jdbcType=INTEGER}


				and LEVEL_2_POS =#{lev2,jdbcType=INTEGER}


			order by LEVEL_1_POS,LEVEL_2_POS
		) t
	) temp
where temp.row_num between #{startRow, jdbcType=INTEGER} and #{endRow, jdbcType=INTEGER}


select 

	from SYS_FUNC


		and ID = #{id,jdbcType=DECIMAL}


		and LEVEL_1_POS =#{lev1,jdbcType=INTEGER}


		and LEVEL_2_POS =#{lev2,jdbcType=INTEGER}


	order by LEVEL_1_POS,LEVEL_2_POS

select FUNC_NAME_EN, FUNC_NAME_ZH_CN, FUNC_NAME_ZH_TW, LEVEL_1_POS, LEVEL_2_POS, (select SESSION_LANG from SYS_USER where USER_CODE = #{other,jdbcType=VARCHAR}) OTHER from SYS_FUNC where TYPE = #{type,jdbcType=VARCHAR}
and LEVEL_1_POS=#{lev1,jdbcType=INTEGER}
order by LEVEL_1_POS,LEVEL_2_POS

SELECT ID, FUNC_CODE, FUNC_NAME_EN, FUNC_NAME_ZH_CN, FUNC_NAME_ZH_TW, TYPE, LEVEL_1_POS, LEVEL_2_POS, (select SESSION_LANG from SYS_USER where USER_CODE = #{other,jdbcType=VARCHAR}) OTHER FROM SYS_FUNC
and TYPE=#{type,jdbcType=VARCHAR}
and ID=#{lev1,jdbcType=INTEGER}
